import {useState} from 'react'

function App() {

  const [city, setCity] = useState('')

  const [weather, setWeather] = useState(null)

  const searchWeather = () => {
    console.log(city)

    const link = "http://api.weatherapi.com/v1/current.json?key="
    const key = "83eb9d090cf6483498a40555222001"
    const url = `${link}${key}&q=${city}`

    fetch(url).then((response) => {
      console.log(response)
      if(response.status === 200) {
        return response.json()
      }
    }).then((data) => {
      console.log(data)

      setWeather(data)
    })
  }

  const handleCityChange = (event) => {
    setCity(event.target.value)
  }

  return (
    <>
      <div>
        <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4">
          <a className="navbar-brand" href="#search">
            Weather
          </a>
        </nav>

        <main className="container " id="search">
          <div className="jumbotron">
            <h1>See the weather in your city</h1>
            <p className="lead">Type the city below</p>

            <div className="mb-4">
              <div>
                <input type="text" className="form-control" value={city} onChange={handleCityChange}/>
              </div>
            </div>

            <button className="btn btn-primary btn-lg btn-block" onClick={searchWeather}>
              Search
            </button>

            {
              weather ? (
                <div className='mt-4 d-flex justify-content-evenly align-items-center'>
                  <div>
                    <img src={weather.current.condition.icon} alt='icon'></img>
                  </div>
                  <div>
                    <h3>{weather.current.condition.text}</h3>
                    <p className='lead'>
                      {weather.current.temp_c}
                    </p>
                  </div>

                </div>

              ) : null
            }

          </div>
        </main>
      </div>
    </>
  )
}

export default App
